// -----------------------------------All products-----------------------------------
const allProducts = [...top_products, ...outwear_products, ...bottoms_products, ...accessories_products];

// Get all products:
const getAllProducts = () => allProducts;
const getAllTopProducts = () => top_products;
const getAllOutwearProducts = () => outwear_products;
const getAllBottomsProducts = () => bottoms_products;
const getAllAccessoriesProducts = () => accessories_products;

// Get random products:
const getProducts = (count) => {
    const max = allProducts.length - count;
    const min = 0;
    const start = Math.floor(Math.random() * (max - min) + min);
    return allProducts.slice(start, start + count);
};

// Get slug products:
const getProductBySlug = (slug) => allProducts.find((e) => e.slug === slug);

// Get cart items:
const getCartItemsInfo = (cartItems) => {
    let res = [];
    if (cartItems.length > 0) {
        cartItems.forEach((e) => {
            let product = getProductBySlug(e.slug);
            res.push({
                ...e,
                product: product,
            });
        });
    }
    // console.log(res)
    // console.log('sorted')
    // console.log(res.sort((a, b) => a.slug > b.slug ? 1 : (a.slug < b.slug ? -1 : 0)))
    return res.sort((a, b) => (a.id > b.id ? 1 : a.id < b.id ? -1 : 0));
};

const productData = {
    getAllProducts,
    getAllTopProducts,
    getAllOutwearProducts,
    getAllBottomsProducts,
    getAllAccessoriesProducts,

    getProducts,

    getProductBySlug,

    getCartItemsInfo,
};

export default productData;
